function Add(n){var a=` <div class="row justify-content-center">\n            <div class="card col-12 col-sm-12 col-md-8 col-lg-6">\n                <div class="card-body">\n                    <h3 class="card-title">\n                        ${n.name}\n                    </h3>\n                    <p>\n                        Language: ${n.language}\n                    </p>\n                    <p>\n                        Github: <a target="_blank" href="${n.html_url}">Visit Repository</a>.\n                    </p>\n                </div>\n            </div>\n        </div>`;$("#c2").append(a)}$(document).ready(function(){$.ajax({url:"https://api.github.com/users/MuhammadRafay151/repos",type:"get",dataType:"json",success:function(n){for(var a=0;a<n.length;a++)Add(n[a])},error:function(n){console.log(n)}})});